import React from 'react'
import Card from './Card';
import { FaAngleLeft, FaAngleRight } from "react-icons/fa6";

export const Testimonials = (props) => {
    let reviews=props.reviews;

  return (
    <div >
       
            <Card data={reviews[0]}/>
            <div className='mt-8 text-lg text-[#a74ef5] '>
            <button onClick={leftShiftHandler} className='p-2'><FaAngleLeft /></button>
            <button onClick={right} className='p-2'><FaAngleRight /></button>
        </div>

        <div>
            <button  class="bg-[#a74ef5] mt-2 mb-0 hover:bg-[#a241f7] text-white font-bold py-2 px-6 rounded-lg " >Surprised Me</button>
        </div>
        
    </div>
  )
}
